import React, { useEffect } from 'react';
import './Home.scss';
import axios from 'axios';

const ApiKey = '8c612c0f3fcf72c15e3efe890d05fc78';
const Url = 'https://api.themoviedb.org/3';
const upcoming = 'upcoming';

const Card = ({ img }) => {
  return <img className='card' src={img} alt='cover' />;
};

const Row = ({ title, arr = [{ img:"https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEh_0NwlZakCVYIqSDM6cwzCzD4B3LnhZ8oXCIRpolRXvjiFcpYfW7axfBk-D4lCWrWpg8843qp7phBxFQHhnh1iitx07YFjBS1s1RX8FkqEqRLCVMQRy7JnhZRdrXxWv4ci96XLw2wc_mfYIMAlkSYofNBIVZVr-Qo5_Bn5_hS8Ma4JFkeRYeM0F_ouAw/s1600/HDgallery%20RRR%20in%20Theatres%20%281%29.jpg"
}] }) => {
  return (
    <div className='row'>
      <h1>{title}</h1>
      <div>
        {arr.map((item) => (
          <Card img={item.img} key={item.img} />
        ))}
      </div>
    </div>
  );
};

const Home = () => {
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${Url}/movie/${upcoming}?api_key=${ApiKey}`);
        console.log(response.data); // Display the fetched data in the console
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  return (
    <section className='home'>
      <div className='banner'></div>
      <Row title={'Popular on Netflix'} />
      <Row title={'Movies'} />
      <Row title={'TV Shows'} />
      <Row title={'My List'} />
      <Row title={'Recently Added'} />
    </section>
  );
};

export default Home;
